import React from "react"

class TodoItem extends React.Component {
render() {
    const style = {
        display: "none"
    }

    return (
        <li>
            <input 
                type="checkbox"
                checked={this.props.todo.completed}
                onChange={() => this.props.handleChange(this.props.todo.id)}
            />
            <label 
                className={this.props.todo.completed ? "completed" : ""}
                onClick={() => this.props.handleChange(this.props.todo.id)}
                style={(this.props.todo.edit) ? style: null}
            >
                {this.props.item}
            </label>
            <input 
                type="text" 
                style={(this.props.todo.edit) ? null: style}
                className="edit-input"
                value={this.props.item}
                onChange={this.props.newValue}
            />
            <div className="edit-delete">
                <span 
                    className="todo-edit" 
                    onClick={(this.props.todo.edit === false) ? () => this.props.handleEdit(this.props.todo.id)
                        : () => this.props.editComplete(this.props.todo.id)}>
                    {(this.props.todo.edit) ? "Save" : "Edit"}
                </span>
                <span 
                    onClick={this.props.onDelete}
                    className="todo-delete fas fa-trash-alt">
                </span>
            </div>
        </li>
    )
}
}

export default TodoItem
