import React from "react"

import TodoItem from "./TodoItem"
import TodoForm from "./TodoForm"

class AddNewTodo extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [],
            value: "",
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
    }

    todoValue = (e) => {
        this.setState({ value: e.target.value })
    }

    handleClick = (e) => {
        this.setState(prevState => {
            const todos = [prevState.value, ...prevState.todos];

            return {
                todos,
                value: "",
                id: "",
                completed: ""
            }
        })
        console.log(this.state.todos)
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState(prevState => {
            const todos = [{
                value: prevState.value,
                completed: 'false'
            }, ...prevState.todos]

            return {
                todos,
                value: "",
                completed: "",
            }
        })
        console.log(this.state.todos)
    }

    render() {
        const todoItem = this.state.todos.map(item => <TodoItem item={item}/>)
        return (
            <div>
                <h4>Todo App</h4>
                <TodoForm />
                <ul>
                    {todoItem}
                </ul>
            </div>
        )
    }
}

export default AddNewTodo
