import React from "react"

import TodoItem from "./TodoItem"

class AddNewTodo extends React.Component {
    constructor() {
        super()
        this.state = {
            todos: ["a","b"],
            value: "",
        }
    }

    handleSubmit = (e) => {
        e.preventDefault()
    }

    todoValue = (e) => {
        this.setState({ value: e.target.value })
    }

    handleClick = (e) => {
        this.setState(prevState => {
            const todos = [prevState.value, ...prevState.todos];

            return {
                todos,
                value: "",
            }
        })
        console.log(this.state.todos)
    }

    handleSubmit = (e) => {
        e.preventDefault();
        let counter = this.state.todos.length + 1
        this.setState(prevState => {
            const todos = [{
                id: counter,
                text: prevState.value,
                completed: false
            }, ...prevState.todos]

            return {
                todos,
                value: ""
            }
        })
        console.log(counter)
    }

    render() {
        const todoItem = this.state.todos.map(item => <TodoItem item={item}/>)
        return (
            <div>
                <h4>Todo App</h4>
                <form className="form-inline justify-content-center" onSubmit={this.handleSubmit}>
                    <input
                        type="text"
                        placeholder="Add Todo"
                        onChange={this.todoValue}
                    />
                    <button
                        type="button"
                        onClick={this.handleClick}
                    >
                    Add</button>
                </form>
                <ul>
                    {todoItem}
                </ul>
            </div>
        )
    }
}

export default AddNewTodo
