import React from "react"

function TodoItem(props) {

    const style = {
        display: "none"
    }

    return (
        <li>
            <input 
                type="checkbox"
                checked={props.todo.completed}
                onChange={() => props.handleChange(props.todo.id)}
            />
            <label 
                className={props.todo.completed ? "completed" : ""}
                onClick={() => props.handleChange(props.todo.id)}
                style={(props.todo.edit) ? style: null}
            >
                {props.item}
            </label>
            <input 
                type="text" 
                style={(props.todo.edit) ? null: style}
                className="edit-input"
                value={props.newValue}
                onChange={props.newValue}
            />
            <div className="edit-delete">
                <span 
                    className="todo-edit" 
                    onClick={(props.todo.edit === false) ? () => props.handleEdit(props.todo.id)
                        : () => props.editComplete(props.todo.id)}>
                    {(props.todo.edit) ? "Save" : "Edit"}
                </span>
                <span 
                    onClick={props.onDelete}
                    className="todo-delete fas fa-trash-alt">
                </span>
            </div>
        </li>
    )
}

export default TodoItem
