import React from "react"

import AddTodo from "./AddTodo.js"
import TodoItem from "./TodoItem.js"


class TodoApp extends React.Component {
    state = {
        todos: [],
        filter: "all",
        newValue: ""
    }

    addTodo = (todo) => {
        this.setState({ 
            todos: [todo, ...this.state.todos] 
        })
    }

    handleChange = (id) => {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed
                }
                return todo
            })
            return {
                todos: updatedTodos
            }
        })
    }

    deleteTodo = (id) => {
        this.setState({
            todos: this.state.todos.filter(todo => todo.id !== id)
        })
    }

    componentDidMount() {
        this.setState({ newValue : this.state.todos.value })
    }

    handleEdit = (id) => {
        this.setState({ newValue : this.state.todos.value })
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.edit = true
                }
                return todo
            })
            return {
                todos: updatedTodos
            }
        })
    }

    newValue = (e) => {
        this.setState({ newValue: e.target.value })
    }

    editComplete = (id) => {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    if (todo.value !== this.state.newValue) {
                        todo.value = this.state.newValue
                    }
                    todo.edit = false
                }
                return todo
            })
            return {
                todos: updatedTodos
            }
        })
        this.setState({ newValue: "" })
        console.log("edit is done")
    }

    updateFilter = (str) => {
        this.setState({
            filter: str
        })
    }

    render() {
        let todos = []
        if (this.state.filter === "all") {
            todos = this.state.todos
        } else if (this.state.filter === "incomplete") {
            todos = this.state.todos.filter(todo => !todo.completed)
        } else if (this.state.filter === "completed") {
            todos = this.state.todos.filter(todo => todo.completed)
        }

        const todoItem = todos.map(item => 
            <TodoItem 
                key={item.id} 
                item={this.state.newValue} 
                handleChange={this.handleChange}
                newValue={this.newValue}
                todo={item}
                onDelete={() => this.deleteTodo(item.id)}
                handleEdit={() => this.handleEdit(item.id)}
                editComplete={() => this.editComplete(item.id)}
            />)

        return (
            <div>
                <h4>Todo App</h4>
                <AddTodo onSubmit={this.addTodo} />
                <br />
                <div className="btnCtr">
                    <button className="btn btn-success" onClick={() => this.updateFilter("completed")}>
                        Sort Completed
                    </button>
                    <button className="btn btn-danger" onClick={() => this.updateFilter("incomplete")}>
                        Sort Incompleted
                    </button>
                    <button className="btn btn-primary" onClick={() => this.updateFilter("all")}>
                        Show All
                    </button>
                </div>
                <ul>
                    {todoItem}
                </ul>
                <br />
                <p>Incompleted Todos: {this.state.todos.filter(todo => !todo.completed).length}</p>
                <p>Completed Todos: {this.state.todos.filter(todo => todo.completed).length}</p>
                <p>Total Todos: {this.state.todos.length}</p>
            </div>
        )
    }
}

export default TodoApp
