import React from "react"

import TodoItem from "./TodoItem"

class AddNewTodo extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            todos: [],
            value: "",
            completed: "",
            item: ""
        }
    }

    handleClick = (e) => {
        let counter = this.state.todos.length + 1
        this.setState(prevState => {
            const todos = [todos, ...prevState.todos];

            return {
                todos,
                completed: "",
                item: "",
                value: "",
            }
        })
        console.log(this.state.todos.length)
    }

    handlePreventDefault = (e) => {
        e.preventDefault()
    }

    handleChange = (id) => {
        this.setState(prevState => {
            const updatedTodos = prevState.todos.map(todo => {
                if (todo.id === id) {
                    todo.completed = !todo.completed
                }
                return todo
            })
            return {
                todos: updatedTodos
            }
        })
    }

    todoValue = (e) => {
        this.setState({ value: e.target.value })
    }

    render() {
        const todoItems = this.state.todos.map(item => (
            <TodoItem key={item} item={item} handleChange={this.handleChange} />
        ))

        return (
            <div className="container">
                <h4>Todo App</h4>
                <form className="form-inline justify-content-center" onSubmit={this.handlePreventDefault}>
                    <input 
                        type="text" 
                        placeholder="Add Todo"
                        value={this.state.value}
                        onChange={this.todoValue}
                    />
                    <button 
                        type="button"
                        onClick={this.handleClick}
                    >
                        Add</button>
                </form>
                <ul>
                    {todoItems}
                </ul>
            </div>
        )
    }
}

export default AddNewTodo
