import React from "react"

import TodoItem from "./TodoItem"
import TodoForm from "./TodoForm"

class AddNewTodo extends React.Component {
    state = {
        todos: []
    }

    handleSubmit = (e) => {
        e.preventDefault()
    }

    handleClick = (e) => {
        this.setState(prevState => {
            const todos = [prevState.value, ...prevState.todos];

            return {
                todos,
                value: "",
                id: "",
                completed: ""
            }
        })
        console.log(this.state.todos)
    }

    addTodo = todo => {
        this.setState({ todo, ...this.state.todos })
    }

    render() {
        const todoItem = this.state.todos.map(item => <TodoItem item={item}/>)
        return (
            <div>
                <h4>Todo App</h4>
                <TodoForm />
                <ul>
                    {todoItem}
                </ul>
            </div>
        )
    }
}

export default AddNewTodo
